mapper.select:


     and suec.coupon_id = #{couponId}
     and suec.user_id = #{userId}
     and suec.coupon_name like concat('%', #{couponName}, '%')
     and suec.coupon_type = #{couponType}
     and suec.coupon_price = #{couponPrice}
     and suec.coupon_no = #{couponNo}
     and suec.coupon_limit = #{couponLimit}
     and suec.coupon_rules = #{couponRules}
     and suec.valid_from = #{validFrom}
     and suec.valid_end = #{validEnd}
     and #{currentTime} >= suec.valid_end
     and suec.quantity = #{quantity}
     and suec.status = #{status}
     and suec.source_from = #{sourceFrom}
     and suec.purpose = #{purpose}
     and suec.purpose_merchant_user_id = #{purposeMerchantUserId}
     and suec.create_by_user_id = #{createByUserId}
     and suec.create_by_name like concat('%', #{createByName}, '%')

group by suec.id
order by suec.create_time desc

mapper.update:
update sys_user_ex_coupon
coupon_id = #{couponId}, user_id = #{userId}, coupon_name = #{couponName}, coupon_type = #{couponType}, coupon_price = #{couponPrice}, coupon_no = #{couponNo}, coupon_limit = #{couponLimit}, coupon_rules = #{couponRules}, valid_from = #{validFrom}, valid_end = #{validEnd}, quantity = #{quantity}, status = #{status}, source_from = #{sourceFrom}, purpose = #{purpose}, purpose_merchant_user_id = #{purposeMerchantUserId}, create_by_user_id = #{createByUserId}, create_by_name = #{createByName}, create_time = #{createTime},
where id = #{id}
