mapper.select:
select vr.id, vr.user_id, vr.start_time, vr.end_time, vr.type, vr.status, vr.remark, vr.operate_user_id, vr.operate_legal_name, vr.time_offset, su.legal_name, vr.create_time, vr.audit_legal_name, vr.audit_time from volunteer_record vr
left join sys_user su on vr.user_id = su.user_id
left join sys_dept d on su.dept_id = d.dept_id


        AND su.legal_name like concat('%', #{legalName}, '%')

     and vr.user_id = #{userId}
     and vr.start_time = #{startTime}
     and vr.end_time = #{endTime}
     and vr.type = #{type}
     and vr.status = #{status}
     and vr.operate_user_id = #{operateUserId}
     and vr.operate_legal_name like concat('%', #{operateLegalName}, '%')

    ${params.dataScope}

order by vr.create_time desc

select vr.id, vr.user_id, vr.start_time, vr.end_time, vr.type, vr.status, vr.remark, vr.operate_user_id, vr.operate_legal_name, vr.time_offset, su.legal_name, vr.create_time, vr.audit_legal_name, vr.audit_time from volunteer_record vr
left join sys_user su on vr.user_id = su.user_id
left join sys_dept d on su.dept_id = d.dept_id


        AND su.legal_name like concat('%', #{legalName}, '%')

     and vr.id = #{id}
     and vr.user_id = #{userId}
     and vr.start_time = #{startTime}
     and vr.end_time = #{endTime}
     and vr.type = #{type}
     and vr.status = #{status}
     and vr.operate_user_id = #{operateUserId}
     and vr.operate_legal_name like concat('%', #{operateLegalName}, '%')

order by vr.create_time desc

select vr.id, vr.user_id, vr.start_time, vr.end_time, vr.type, vr.status, vr.remark, vr.operate_user_id, vr.operate_legal_name, vr.time_offset, su.legal_name, vr.create_time, vr.audit_legal_name, vr.audit_time from volunteer_record vr
left join sys_user su on vr.user_id = su.user_id
left join sys_dept d on su.dept_id = d.dept_id


        AND su.legal_name like concat('%', #{legalName}, '%')

     and vr.id = #{id}
     and vr.user_id = #{userId}
     and vr.start_time = #{startTime}
     and vr.end_time = #{endTime}
     and vr.type = #{type}
     and vr.status = #{status}
     and vr.operate_user_id = #{operateUserId}
     and vr.operate_legal_name like concat('%', #{operateLegalName}, '%')

order by vr.create_time desc LIMIT 1

mapper.update:
update volunteer_record
user_id = #{userId}, start_time = #{startTime}, end_time = #{endTime}, type = #{type}, status = #{status}, remark = #{remark}, time_offset = #{timeOffset}, audit_legal_name = #{auditLegalName}, audit_time = sysdate()
where id = #{id}
